var pg = require('pg');
var orm = require('orm');
function create_db_client(){
    var connectionString = "postgres://postgres:1234@localhost/dtt_main";
    var client_db = new pg.Client(connectionString);
    client_db.connect();
    return client_db;
};

var _entity={};
var db = orm.connect("postgres://postgres:1234@localhost/dtt_main",function (success,db){
    _entity.ChatRoom = db.define('chat_room',{
        name : String
    });
});



module.exports = {
    core: {
        create_db_client : create_db_client,
        entity: _entity
    }
};

