var pg = require('pg');
var orm = require('orm');

var _entity={};
var db = orm.connect("postgres://postgres:1234@localhost/dtt_main",function (success,db){
    _entity.ChatRoom = db.define('chat_room',{
        name : String
    });
});

module.exports = {
    core: {
        entity_factory: {
            create : function(entity_name,params){
                return new _entity[entity_name](params)
            }
        }
    }
};

